import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
   article=[
    {
      "source": {
        "id": "msnbc",
        "name": "MSNBC"
      },
      "author": null,
      "title": "Rep. Schiff: Mark Meadows has ‘no moral compass’",
      "description": "Rep. Adam Schiff: “People like Mark Meadows, people like Jim Jordan, people like Kevin McCarthy and Mitch McConnell understood exactly the danger Donald Trump posed but standing up to him was not as important as the position they held or the one they wanted.”",
      "url": "https://www.msnbc.com/all-in/watch/rep-schiff-mark-meadows-has-no-moral-compass-159016517802",
      "urlToImage": "https://media-cldnry.s-nbcnews.com/image/upload/t_nbcnews-fp-1200-630,f_auto,q_auto:best/mpx/2704722219/2022_12/1672363736227_n_hayes_schiff_221229_1920x1080-mnxrjj.jpg",
      "publishedAt": "2022-12-30T01:30:20Z",
      "content": null
    },
    {
      "source": {
        "id": "cbs-news",
        "name": "CBS News"
      },
      "author": "CBS News",
      "title": "House panel expected to release Trump's tax returns Friday",
      "description": "A key House panel is expected to release portions of former President Donald Trump's tax returns Friday. The House Ways and Means Committee voted last week to make six years of Trump's returns public. CBS News congressional correspondent Scott MacFarlane disc…",
      "url": "https://www.cbsnews.com/video/house-panel-expected-to-release-donald-trump-tax-returns-friday/",
      "urlToImage": "https://assets1.cbsnewsstatic.com/hub/i/r/2022/12/30/6bd6c5e4-50c2-43e1-92b3-27a6826dad52/thumbnail/1200x630/04b863b3de74c247063ba2a4e7ae17d6/cbsn-fusion-house-panel-to-release-trumps-tax-returns-friday-thumbnail-1584477-640x360.jpg",
      "publishedAt": "2022-12-30T00:29:17+00:00",
      "content": "Watch CBS News\r\nCopyright ©2022 CBS Interactive Inc. All rights reserved.\r\nGet browser notifications for breaking news, live events, and exclusive reporting.\r\nNot NowTurn On"
    },
    {
      "source": {
        "id": "msnbc",
        "name": "MSNBC"
      },
      "author": "Ja&#x27;han Jones",
      "title": "Report: Greene told Trump there would be a QAnon presence on Jan. 6",
      "description": "Former White House aide Cassidy Hutchinson testified that Rep. Marjorie Taylor Greene of Georgia told Donald Trump and Mark Meadows about QAnon ahead of Jan. 6.",
      "url": "https://www.msnbc.com/the-reidout/reidout-blog/greene-qanon-trump-hutchinson-rcna63622",
      "urlToImage": "https://media-cldnry.s-nbcnews.com/image/upload/t_nbcnews-fp-1200-630,f_auto,q_auto:best/rockcms/2022-12/221229-marjorie-taylor-greene-cs-d2dd3e.jpg",
      "publishedAt": "2022-12-30T00:16:46Z",
      "content": "On Tuesday, the House Jan. 6 committee released transcripts from former White House aide Cassidy Hutchinson. Hutchinson, who worked for White House chief of staff Mark Meadows, was privy to some of t… [+2426 chars]"
    },
    {
      "source": {
        "id": "msnbc",
        "name": "MSNBC"
      },
      "author": null,
      "title": "MAGAworld rocked by Jan. 6 testimony bomb: Top advisors knew of plan to march on Capitol",
      "description": "The Jan. 6 committee releasing stunning new interview transcripts from Donald Trump Jr. and Stephen Miller. Miller telling the committee \"I think there might be a plan for a peaceful walk to the Capitol...\" Kimberly Guilfoyle also revealing that Don Jr. didn’…",
      "url": "https://www.msnbc.com/the-beat-with-ari/watch/magaworld-rocked-by-jan-6-testimony-bomb-top-advisors-knew-of-plan-to-march-on-capitol-159010885895",
      "urlToImage": "https://media-cldnry.s-nbcnews.com/image/upload/t_nbcnews-fp-1200-630,f_auto,q_auto:best/mpx/2704722219/2022_12/1672360176564_n_ari_testimony_221229_1920x1080-ntdqjy.jpg",
      "publishedAt": "2022-12-29T23:41:18Z",
      "content": null
    }, 
  ]
  constructor(props){
    super(props);
    console.log("I am constructor");
    this.state={
      articles:this.article,
      loading:false
    }
  }
  async componentDidMount(){ //run cdm after render
    let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=704830d7e65c48c5b76ecc4cdb151d51";
    let data = await fetch(url);
    let parsedData =await data.json() //async & await work as rey/catch block and it will work as promise.
    // console.log(parsedData);
    this.setState({articles:parsedData.article});
  }
  render() {
    return (
      
  <div className="container my-3">
    
        <h2>NewsHunt - Top Headlines</h2>
       
     <div className="row my-3">
     {this.state.articles.map((element)=>{
   return <div className="col-md-4 my-3 " key={element.url}>
    <NewsItem  title={element.title.slice(0,35)} descr={element.description.slice(0,80)} imageUrl={element.urlToImage} newsUrl={element.url}/>
  </div>
      }
      )
   }
           
         </div>
     </div>

    )
  }
}

export default News
